import React, {Component} from 'react';

export class Input extends Component {
    state={
        value:''
    }

    handleChange=(ev)=>{
        this.setState ({value: ev.target.value})
    }
    render (){
        return <>
        <p>{this.state.value}</p>
        <input type='text' value={this.state.value} onChange={this.handleChange}></input>
        </>
    }
}
