import React, {Component} from 'react';
import {Input} from './Input';
import {Button} from './Button';

export class Form extends Component {
    state ={
        name:'click',
        value:'',
        messages: []
    }
handleClick =() =>{
    console.log('ccccc');
    this.setState({messages:[...this.state.messages, this.state.value]});
    this.setState({value: ''});
    }


handleChange =(ev) =>{
  //  console.log('xxxx');
    this.setState({value: ev.target.value});
}

    render (){
        return <>
           <ul>
        {this.state.messages.map(message =>
            <li>{message}</li>
        )}
        </ul>
            <Input change={this.handleChange} value={this.state.value}/>
            <Button click ={this.handleClick} name={this.state.name}/>
            <button onClick={()=> this.setState({visible:!visible})}>show</button>
        </> 
    }
}
