import React, {Component} from 'react';

export class Input extends Component {
    state={
        value:''
    }

    handleChange=(ev)=>{
        this.setState ({value:eval.target.value})
    }
    render (){
        return <input type='text' value={this.state.value} onChange={this.handleChange}></input>
    }
}
